import React from 'react';
import { Box, Stack, Typography } from '@mui/material';
import HeroBannerImage from '../assets/images/banner.png';
import './HeroBanner.css';

const HeroBanner = () => (
  <Box
    sx={{
      mt: { lg: '100px', xs: '50px' },
      ml: { sm: '50px' },
      p: '20px',
      position: 'relative',
      height: { lg: '600px', xs: '400px' },
      display: 'flex',
      flexDirection: { lg: 'row', xs: 'column' },
      alignItems: 'center',
    }}
    className="hero-banner-container"
  >
    <Box
      sx={{
        flex: 1,
        textAlign: 'left',
        zIndex: 1,
        animation: 'fadeIn 2s',
        paddingRight: { lg: '20px', xs: '0px' },
      }}
    >
      <Typography color="#FF2625" fontWeight="600" fontSize="36px">
        Fitness Club
      </Typography>
      <Typography
        fontWeight={700}
        sx={{
          fontSize: { lg: '64px', xs: '48px' },
          mb: '23px',
          mt: '30px',
        }}
      >
        Sweat, Smile <br />
        And Repeat
      </Typography>
      <Typography fontSize="24px" fontFamily="Alegreya" lineHeight="35px">
        Check out the most effective exercises personalized to you
      </Typography>
      <Stack
        sx={{
          alignItems: 'flex-start',
          mt: '45px',
        }}
      >
        <a
          href="#exercises"
          style={{
            textDecoration: 'none',
            width: '200px',
            textAlign: 'center',
            background: '#FF2625',
            padding: '14px',
            fontSize: '22px',
            textTransform: 'none',
            color: 'white',
            borderRadius: '4px',
          }}
        >
          Explore Exercises
        </a>
      </Stack>
    </Box>
    <Box
      component="img"
      src={HeroBannerImage}
      alt="hero-banner"
      sx={{
        flex: 1,
        position: { lg: 'relative', xs: 'absolute' },
        bottom: { lg: '0', xs: '0' },
        right: { lg: '0', xs: '0' },
        width: { lg: '600px', md: '500px', sm: '400px', xs: '300px' },
        animation: 'fadeIn 2s 2.5s',
      }}
      className="hero-banner-img"
    />
  </Box>
);

export default HeroBanner;
