import React, { useState } from 'react';
import { Box, Button, Card, CardContent, CardMedia, Typography, Tabs, Tab } from '@mui/material';

// Product data with categories
const products = [
  {
    id: 1,
    name: 'Dumbbells',
    image: 'https://via.placeholder.com/150', // Replace with actual image URLs
    price: '$50',
    category: 'Equipment'
  },
  {
    id: 2,
    name: 'Yoga Mat',
    image: 'https://via.placeholder.com/150', // Replace with actual image URLs
    price: '$20',
    category: 'Equipment'
  },
  {
    id: 3,
    name: 'Whey Protein',
    image: 'https://via.placeholder.com/150', // Replace with actual image URLs
    price: '$30',
    category: 'Supplements'
  },
  {
    id: 4,
    name: 'Creatine',
    image: 'https://via.placeholder.com/150', // Replace with actual image URLs
    price: '$25',
    category: 'Supplements'
  },
  {
    id: 5,
    name: 'Resistance Bands',
    image: 'https://via.placeholder.com/150', // Replace with actual image URLs
    price: '$15',
    category: 'Equipment'
  },
  {
    id: 6,
    name: 'BCAA',
    image: 'https://via.placeholder.com/150', // Replace with actual image URLs
    price: '$35',
    category: 'Supplements'
  },
  // Add more products here
];

const Shop = () => {
  const [category, setCategory] = useState('All');
  const [cart, setCart] = useState([]);

  // Filter products based on selected category
  const filteredProducts = category === 'All' ? products : products.filter(product => product.category === category);

  // Handle category change
  const handleCategoryChange = (event, newValue) => {
    setCategory(newValue);
  };

  // Function to add products to the cart
  const addToCart = (product) => {
    setCart([...cart, product]);
    console.log('Added to Cart:', product);
  };

  return (
    <Box sx={{ p: 3, backgroundColor: '#1c1c1c', color: '#e0e0e0', minHeight: '100vh' }}>
      <Typography variant="h3" sx={{ mb: 3, textAlign: 'center', color: '#ff2625' }}>Shop</Typography>

      {/* Tabs for category selection */}
      <Tabs
        value={category}
        onChange={handleCategoryChange}
        indicatorColor="primary"
        textColor="inherit"
        centered
        sx={{
          mb: 3,
          '.Mui-selected': { color: '#ff2625' }
        }}
      >
        <Tab label="All" value="All" />
        <Tab label="Equipment" value="Equipment" />
        <Tab label="Supplements" value="Supplements" />
      </Tabs>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3, justifyContent: 'center' }}>
        {filteredProducts.map(product => (
          <Card
            key={product.id}
            sx={{
              maxWidth: 345,
              backgroundColor: '#2b2b2b',
              color: '#e0e0e0',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
              '&:hover': {
                boxShadow: '0 6px 12px rgba(0, 0, 0, 0.5)',
                transform: 'translateY(-5px)',
                transition: 'transform 0.3s ease-in-out'
              }
            }}
          >
            <CardMedia
              component="img"
              height="140"
              image={product.image}
              alt={product.name}
              sx={{
                '&:hover': {
                  transform: 'scale(1.05)',
                  transition: 'transform 0.3s ease-in-out'
                }
              }}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {product.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {product.price}
              </Typography>
              <Button
                variant="contained"
                sx={{
                  mt: 2,
                  backgroundColor: '#ff2625',
                  '&:hover': {
                    backgroundColor: '#e6201d'
                  }
                }}
                onClick={() => addToCart(product)}
              >
                Add to Cart
              </Button>
            </CardContent>
          </Card>
        ))}
      </Box>

      {/* Display cart contents */}
      <Box sx={{ mt: 5, color: '#e0e0e0' }}>
        <Typography variant="h4" sx={{ mb: 2, textAlign: 'center', color: '#ff2625' }}>Cart</Typography>
        {cart.length === 0 ? (
          <Typography sx={{ textAlign: 'center' }}>Your cart is empty.</Typography>
        ) : (
          cart.map((item, index) => (
            <Box key={index} sx={{ textAlign: 'center', mb: 1 }}>
              <Typography>{item.name} - {item.price}</Typography>
            </Box>
          ))
        )}
      </Box>
    </Box>
  );
};

export default Shop;
